import { Button, Col, Input, Row, Space, Card } from "antd";
import { useState } from "react";

import { Image } from "antd";
import { ArrowLeftOutlined, ArrowRightOutlined } from "@ant-design/icons";

const Base64ImageConverter = () => {
  const [image, setImage] = useState("");
  const [base64, setBase64] = useState("");
  const fallbackImage =
    "data:image/png;base64,";
  const convert = (type) => {
    if (type === "b2i") {
      setImage(
        base64.startsWith("data:image/")
          ? base64
          : `data:image/*;base64,${base64}`
      );
    } else if (type === "i2b") {
      setBase64(image);
    }
  };
  return (
    <Row gutter={20}>
      <Col span={24}>
        <Card
          title="控制区"
          size="small"
          style={{ width: "50%", textAlign: "center", margin: "10px auto" }}
        >
          <Space size="middle" style={{ textAlign: "center" }}>
            <Button type="primary" onClick={() => convert("b2i")}>
              <ArrowLeftOutlined /> Base64还原图片
            </Button>
            <Button type="primary" onClick={() => convert("i2b")}>
              图片转换Base64
              <ArrowRightOutlined />
            </Button>
          </Space>
        </Card>
      </Col>
      <Col span={12} style={{ textAlign: "right" }}>
        <Image
          width="400px"
          src={image}
          preview={{ mask: <span>点击查看大图</span> }}
          fallback={fallbackImage}
        />
      </Col>
      <Col span={12}>
        <Space direction="vertical" size="middle" style={{ display: "flex" }}>
          <Input.TextArea
            rows={15}
            style={{ width: 400, height: 400 }}
            value={base64}
            onChange={(e) => {
              setBase64(e.target.value);
            }}
          />
        </Space>
      </Col>
    </Row>
  );
};

export default Base64ImageConverter;
